mapper.update:
update board
set	del = 1
where bno = #{bno}

mapper.select:
select id, name
from springuser
where id=#{id} and password = #{password}

mapper.update:
update board_reply
set rewriter = #{rewriter}, rememo = #{rememo}, redate = curdate()
where reno = #{reno}

update board_reply
set redel = 1
where reno = #{reno}

mapper.select:
select
@rownum:=@rownum+1 as no, board.*
from board
where (@rownum:=0)=0 and del = 0
order by bno desc
limit #{start}, #{cntPerPage}
